import React from 'react';
import { Text, View, Image, TextInput, TouchableOpacity } from 'react-native';

// import css from './Style'
import css from './pages/Home/Style'

export default function Login() {
  return (
    <View style={css.container}>
      <Image style={css.image} source={require('./pages/assets/logo.PNG')}/>
      <View style={css.card}>
        <Text style={{fontWeight: 'bold', fontSize: 18}}>LOGIN</Text>
        <TextInput placeholder={"Matrícula..."} style={css.input}></TextInput>
        <TextInput placeholder={"Senha..."} style={css.input}></TextInput>
        <Text style={css.botao1}>Esqueci a senha</Text>
        <TouchableOpacity style={css.botao2}>
          <Text style={css.botaoText}>Entrar</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}
