import { StyleSheet, Text, View, Image, TextInput, TouchableOpacity} from 'react-native';

export default function App() {
  return (
    <View style={styles.container}>
      <Image style={styles.image} source={require('./pages/assets/logo.PNG')}/>
      <View style={styles.card}>
        <Text style={{fontWeight: 'bold', fontSize: 18}}>LOGIN</Text>
        <TextInput placeholder={"Matrícula..."} style={styles.input}></TextInput>
        <TextInput placeholder={"Senha..."} style={styles.input}></TextInput>
        <Text style={styles.botao}>Esqueci a senha</Text>
        <TouchableOpacity>
          <Text style={{color:"blue", fontSize: 18}}>Entrar</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center'
  },
  image:{
    width: 150,
    height: 110,
    marginTop: "5%"
  },
  card:{
    backgroundColor: '#E8ECED',
    width: "60%",
    height: "50%",
    borderRadius: 5,
    alignItems: 'center',
    justifyContent: 'space-evenly',
    marginTop: "15%",
    shadowOffset: {width: -2, height: 8},
    shadowOpacity: 0.3,
    shadowRadius: 5,
  },
  input:{
    borderBottomWidth: 1,
    borderBottomColor: 'black',
    padding: "2%",
  },
  botao:{
    color:"blue",
    fontSize: 12,
    marginRight: -60,
    marginTop: -20,
    shadowOffset: {width: -2, height: 8},
    shadowOpacity: 0.3,
    shadowRadius: 5,
  }
});
