import { StyleSheet, Text, View, Image, TextInput, TouchableOpacity} from 'react-native';

export default function App() {
  return (
    <View style={styles.container}>
      <Image style={styles.image} source={require('./pages/assets/logo.PNG')}/>
      <View style={styles.card}>
        <Text style={{fontWeight: 'bold'}}>Login</Text>
        <TextInput placeholder={"Matrícula..."} style={styles.input}></TextInput>
        <TextInput placeholder={"Senha..."} style={styles.input}></TextInput>
        <Text style={{color:"blue", fontSize: 12}}>Esqueci a senha</Text>
        <TouchableOpacity>
          <Text style={{color:"blue", fontSize: 14}}>Entrar</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
  },
  image:{
    width: 140,
    height: 100,
    marginTop: 20
  },
  card:{
    backgroundColor: '#E8ECED',
    width: "60%",
    height: "50%",
    borderRadius: 5,
    alignItems: 'center',
    justifyContent: 'space-evenly',
  },
  input:{
    borderBottomWidth: 1,
    borderBottomColor: 'black'
  }
});
